static int	ft_isdigit(int c)
{
	return (c >= '0' && c <= '9');
}

static int	ft_isalpha(int c)
{
	return ((c >= 'a' && c <= 'z') || (c >= 'A' && c <= 'Z'));
}

int			ft_isalnum(int c)
{
	return (ft_isdigit(c) || ft_isalpha(c));
}
