#include "mat.h"

inline static float		mat4f_determinant_love_norme(const float m[4][4])
{
	return ((m[0][2] * (m[1][0] * m[2][1] * m[3][3]
	- m[1][0] * m[2][3] * m[3][1]
	- m[1][1] * m[2][0] * m[3][3]
	+ m[1][1] * m[2][3] * m[3][0]
	+ m[1][3] * m[2][0] * m[3][1]
	- m[1][3] * m[2][1] * m[3][0]))
	- (m[0][3] * (m[1][0] * m[2][1] * m[3][2]
	- m[1][0] * m[2][2] * m[3][1]
	- m[1][1] * m[2][0] * m[3][2]
	+ m[1][1] * m[2][2] * m[3][0]
	+ m[1][2] * m[2][0] * m[3][1]
	- m[1][2] * m[2][1] * m[3][0])));
}

inline float			mat4f_determinant(const float m[4][4])
{
	return ((m[0][0] * (m[1][1] * m[2][2] * m[3][3]
	- m[1][1] * m[2][3] * m[3][2]
	- m[1][2] * m[2][1] * m[3][3]
	+ m[1][2] * m[2][3] * m[3][1]
	+ m[1][3] * m[2][1] * m[3][2]
	- m[1][3] * m[2][2] * m[3][1]))
	- (m[0][1] * (m[1][0] * m[2][2] * m[3][3]
	- m[1][0] * m[2][3] * m[3][2]
	- m[1][2] * m[2][0] * m[3][3]
	+ m[1][2] * m[2][3] * m[3][0]
	+ m[1][3] * m[2][0] * m[3][2]
	- m[1][3] * m[2][2] * m[3][0])) + mat4f_determinant_love_norme(m));
}

inline static double	mat4d_determinant_love_norme(const double m[4][4])
{
	return ((m[0][2] * (m[1][0] * m[2][1] * m[3][3]
	- m[1][0] * m[2][3] * m[3][1]
	- m[1][1] * m[2][0] * m[3][3]
	+ m[1][1] * m[2][3] * m[3][0]
	+ m[1][3] * m[2][0] * m[3][1]
	- m[1][3] * m[2][1] * m[3][0]))
	- (m[0][3] * (m[1][0] * m[2][1] * m[3][2]
	- m[1][0] * m[2][2] * m[3][1]
	- m[1][1] * m[2][0] * m[3][2]
	+ m[1][1] * m[2][2] * m[3][0]
	+ m[1][2] * m[2][0] * m[3][1]
	- m[1][2] * m[2][1] * m[3][0])));
}

inline double			mat4d_determinant(const double m[4][4])
{
	return ((m[0][0] * (m[1][1] * m[2][2] * m[3][3]
	- m[1][1] * m[2][3] * m[3][2]
	- m[1][2] * m[2][1] * m[3][3]
	+ m[1][2] * m[2][3] * m[3][1]
	+ m[1][3] * m[2][1] * m[3][2]
	- m[1][3] * m[2][2] * m[3][1]))
	- (m[0][1] * (m[1][0] * m[2][2] * m[3][3]
	- m[1][0] * m[2][3] * m[3][2]
	- m[1][2] * m[2][0] * m[3][3]
	+ m[1][2] * m[2][3] * m[3][0]
	+ m[1][3] * m[2][0] * m[3][2]
	- m[1][3] * m[2][2] * m[3][0])) + mat4d_determinant_love_norme(m));
}
