#include <string.h>

char	*ft_strncat(char *s1, const char *s2, size_t n)
{
	char *src;

	src = s1;
	while (*s1)
		s1++;
	while (*s2 && n--)
		*s1++ = *s2++;
	*s1 = '\0';
	return (src);
}
